import React, { useEffect, useReducer } from "react";
import { CopyToClipboard } from "react-copy-to-clipboard";

const initialState = {
  shortenLink: "",
  copied: false,
  loading: false,
  error: false,
};
const ResultLink = (props) => {
  useEffect(() => {
    const Fetcher = async () => {
      try {
        const response = await fetch(
          `https://api.shrtco.de/v2/shorten?url=${props.link}`
        );
        const result = response.data.result.full_short_link;
        return {
          shortenLink: result,
        };
      } catch (err) {
        return {
          error: err,
        };
      } finally {
        return {
          loading: false,
        };
      }
    };
  },[link]);
  const [state, dispatch] = useReducer(Fetcher, initialState);
  return (
    <div className="shorted-container">
      <div className="shorted-link"></div>
      <CopyToClipboard>
        <button className="copy-btn">Copy</button>
      </CopyToClipboard>
    </div>
  );
};

export default ResultLink;
