import React, { useState } from "react";
import { CopyToClipboard } from "react-copy-to-clipboard";
import axios from "axios";
//Declaring initial State
//Dispatch function : Fetcher

//Result Link Component
const ResultLink = (props) => {
  const getLink = props.link
  console.log(getLink)
  const [shorted, setShorted] = useState("");
  const [copied, setCopied] = useState(false);
  console.log(input)
  try {
    if (input.length >= 1) {
      console.log(input);
      axios
        .get(`https://api.shrtco.de/v2/shorten?url=${input}`)
        .then((response) => {
          console.log(response.data.result.full_short_link);
          setShorted(response.data.result.full_short_link);
          if(response){
            // setInput('')
          }
        })
        .catch((err) => console.log(err.message));
    }
  } catch (e) {
    console.log(e);
  }
  const handleCopy = () => {
    setCopied(true);
  };
  if(copied){
    setTimeout(()=>{
      setCopied(false)
    },1000)
  }
  return (
    <>
      {shorted && (
        <div className="shorted-container">
          <div className="shorted-link">{shorted}</div>
          <CopyToClipboard text={shorted} onCopy={handleCopy}>
            <button className={copied ? "copied" : "copy-btn"}>Copy</button>
          </CopyToClipboard>
        </div>
      )}
    </>
  );
};

export default ResultLink;
