import React, { useEffect, useReducer, useState} from "react";
import { CopyToClipboard } from "react-copy-to-clipboard";
import axios from "axios";
//Declaring initial State
const initialState = {
  inputlink: "",
  shortenLink: "",
  copied: false,
  loading: false,
  error: false,
};
//Dispatch function : Fetcher

//Result Link Component
const ResultLink = (props) => {
  const [shorted,setShorted] = useState('')
  const fetcher = async (state) => {
    try {
      const response = await axios.get(
        `https://api.shrtco.de/v2/shorten?url=${state.inputlink}`
      );
      const shorted = response.data.result.full_short_link;
      state.shortenLink = `${shorted}`;
      setShorted(shorted)
      // return state;
      return {
        inputlink: "",
        shortenLink: `${shorted}`,
        copied: false,
        loading: false,
        error: false,
      }
    } catch (err) {
      state.error = err;
      return state;
    } finally {
      state.loading = false;
      return state;
    }
  };
  const [state, dispatch] = useReducer(fetcher, initialState);
  //use Effect to call dispatch function
  useEffect(() => {
    if (props.link.length) {
      state.inputlink = `${props.link}`;
      dispatch(state);
      console.log('inside effect',state)
      let obj = state;
      console.log(obj.copied,obj.loading,obj.inputlink,obj.shortenLink)
    }
  }, [props.link]);
  console.log("shortenLink", state);
  //use Effect to make some delay while getting response
  useEffect(() => {
    const delay = setTimeout(() => {
      return {
        copied: false,
      };
    }, 1000);
    return () => clearTimeout(delay);
  }, [state.copied]);
  if (state.loading) {
    return <div className="loading">Loading...</div>;
  }
  if (state.error) {
    return <div className="noData">Something went wrong...</div>;
  }
  return (
    <>
      {shorted && (
        <div className="shorted-container">
          <div className="shorted-link">{shorted}</div>
          <CopyToClipboard
            text={state.shortenLink}
            onCopy={() => {
              return {
                copied: true,
              };
            }}
          >
            <button className={state.copied ? "copied" : "copy-btn"}>
              Copy
            </button>
          </CopyToClipboard>
        </div>
      )}
    </>
  );
};

export default ResultLink;
