import { useState } from "react";
import ContentBG from "./Components/ContentBG";
import Header from "./Components/Header";
// import InputForm from './Components/InputForm';
// import ResultLink from './Components/ResultLink';
import "./App.css";

const InputForm = (props) => {
  //State for the value entered in text box
  const [value, setValue] = useState("");
  //function to pass the pasted link to the resultlink component
  const handleShorten = () => {
    // console.log('handleShorten called',value)
    props.setLink(value);
    setValue("");
  };
  return (
    <div className="inputForm">
      <input
        type="text"
        value={value}
        placeholder="Paste link here to short it..."
        onChange={(event) => setValue(event.target.value)}
      />
      <button onClick={handleShorten}>Shorten</button>
    </div>
  );
};

const ResultLink = (props) => {
  const [shorted, setShorted] = useState("");
  const [copied, setCopied] = useState(false);
  const input = props.link;
  try {
    if (input.length >= 1) {
      console.log(input);
      axios
        .get(`https://api.shrtco.de/v2/shorten?url=${input}`)
        .then((response) => {
          console.log(response.data.result.full_short_link);
          setShorted(response.data.result.full_short_link);
        })
        .catch((err) => console.log(err.message));
    }
  } catch (e) {
    console.log(e);
  }
  const handleCopy = () => {
    setCopied(true);
  };
  if (copied) {
    setTimeout(() => {
      document.getElementById("disabled").disabled = true;
      setCopied(false);
    }, 1000);
  }
  return (
    <>
      {shorted && (
        <div className="shorted-container">
          <div className="shorted-link">{shorted}</div>
          <CopyToClipboard text={shorted} onCopy={handleCopy}>
            <button id="disabled" className={copied ? "copied" : "copy-btn"}>
              Copy
            </button>
          </CopyToClipboard>
        </div>
      )}
    </>
  );
};
function App() {
  const [link, setLink] = useState("");
  return (
    <>
      <Header />
      <InputForm setLink={setLink} />
      <ContentBG />
      <ResultLink link={link} />
    </>
  );
}

export default App;
