import React, { useEffect, useReducer } from "react";
import { CopyToClipboard } from "react-copy-to-clipboard";
import axios from "axios";

const initialState = {
  shortenLink: "",
  copied: false,
  loading: false,
  error: false,
};
const fetcher = async () => {
  console.log("fetcher called", props.link);
  try {
    const response = await axios(
      `https://api.shrtco.de/v2/shorten?url=${props.link}`
    );
    const result = response.data.result.full_short_link;
    state.shortenLink = result;
    console.log("changed", state.shortenLink);
    // return {
    //   shortenLink: result,
    // };
  } catch (err) {
    state.error = err;
  } finally {
    state.loading = false;
  }
};
const ResultLink = (props) => {

  const [state, dispatch] = useReducer(fetcher, initialState);
  useEffect(() => {
    if (props.link.length) {
      dispatch();
    }
  }, [props.link]);

  useEffect(() => {
    const delay = setTimeout(() => {
      return {
        copied: false,
      };
    }, 1000);
    return () => clearTimeout(delay);
  }, [state.copied]);
  if (state.loading) {
    return <div className="loading">Loading...</div>;
  }
  if (state.error) {
    return <div className="noData">Something went wrong...</div>;
  }
  console.log("shortenLink", state.shortenLink);
  return (
    <>
      {state.shortenLink && (
        <div className="shorted-container">
          <div className="shorted-link">{state.shortenLink}</div>
          <CopyToClipboard
            text={state.shortenLink}
            onCopy={() => {
              return {
                copied: true,
              };
            }}
          >
            <button className={state.copied ? "copied" : "copy-btn"}>
              Copy
            </button>
          </CopyToClipboard>
        </div>
      )}
    </>
  );
};

export default ResultLink;
