import React, { useEffect, useReducer} from "react";
import { CopyToClipboard } from "react-copy-to-clipboard";
import axios from "axios";

const initialState = {
  inputlink: "",
  shortenLink: "",
  copied: false,
  loading: false,
  error: false,
};
const fetcher = async (state) => {
  try {
    const response = await axios(
      `https://api.shrtco.de/v2/shorten?url=${state.inputlink}`
    );
    const shorted = response.data.result.full_short_link;
    state.shortenLink = `${shorted}`;
    // return state;
    return {
      inputlink: "",
      shortenLink: `${response.data.result.full_short_link}`,
      copied: false,
      loading: false,
      error: false,
    }
  } catch (err) {
    state.error = err;
    return state;
  } finally {
    state.loading = false;
    return state;
  }
};
const ResultLink = (props) => {
  const [state, dispatch] = useReducer(fetcher, initialState);
  useEffect(() => {
    if (props.link.length) {
      state.inputlink = `${props.link}`;
      dispatch(state);
      console.log('inside effect',state)
      let obj = state;
      console.log(obj.copied,obj.loading,obj.inputlink,obj.shortenLink)
    }
  }, [props.link]);
  console.log("shortenLink", state);
  useEffect(() => {
    const delay = setTimeout(() => {
      return {
        copied: false,
      };
    }, 1000);
    return () => clearTimeout(delay);
  }, [state.copied]);
  if (state.loading) {
    return <div className="loading">Loading...</div>;
  }
  if (state.error) {
    return <div className="noData">Something went wrong...</div>;
  }
  return (
    <>
      {state.shortenLink && (
        <div className="shorted-container">
          <div className="shorted-link">{state.shortenLink}</div>
          <CopyToClipboard
            text={state.shortenLink}
            onCopy={() => {
              return {
                copied: true,
              };
            }}
          >
            <button className={state.copied ? "copied" : "copy-btn"}>
              Copy
            </button>
          </CopyToClipboard>
        </div>
      )}
    </>
  );
};

export default ResultLink;
