import React, { useEffect, useReducer } from "react";
import { CopyToClipboard } from "react-copy-to-clipboard";
import axios from "axios";

const initialState = {
  shortenLink: "",
  copied: false,
  loading: false,
  error: false,
};

const ResultLink = (props) => {
  const fetcher = async () => {
    console.log('fetcher called',props.link)
    try {
      const response = await axios(
        `https://api.shrtco.de/v2/shorten?url=${props.link}`
      );
      console.log
      const result = response.data.result.full_short_link;
      return {
        loading: true,
        shortenLink: result,
      };
    } catch (err) {
      return {
        error: err,
      };
    } finally {
      return {
        loading: false,
      };
    }
  };

  const [state, dispatch] = useReducer(fetcher, initialState);
  useEffect(() => {
    if (props.link) {
      dispatch();
    }
  }, [props.link]);

  useEffect(() => {
    const delay = setTimeout(() => {
      return {
        copied: false,
      };
    }, 1000);
    return () => clearTimeout(delay);
  }, [state.copied]);
  if (state.loading) {
    return <div className="loading">Loading...</div>;
  }
  if (state.error) {
    return <div className="noData">Something went wrong...</div>;
  }

  return (
    <>
      {state.shortenLink && (
        <div className="shorted-container">
          <div className="shorted-link">{state.shortenLink}</div>
          <CopyToClipboard
            text={state.shortenLink}
            onCopy={() => {
              return {
                copied: true,
              };
            }}
          >
            <button className={state.copied ? "copied" : "copy-btn"}>
              Copy
            </button>
          </CopyToClipboard>
        </div>
      )}
    </>
  );
};

export default ResultLink;
