import React from 'react'
import { useSelector , useDispatch} from 'react-redux'
const App = () => {
  const theme = useSelector((state)=>state.changeLight)
  return (
    <div className={theme === 'lit' ? 'lit' : 'dark'}>
      <button>enable DarkMode</button>
    </div>
  )
}

export default App
