import React, {useState} from 'react';
import TaskList from './TaskList';
import {RiCloseCircleLine} from 'react-icons/ri';
import {TiEdit} from 'react-icons/ti';
import AddTask from './AddTask';

function Todo(props) {
    const [edit,setEdit] = useState({
        id:null,
        value:''
    })

    const submitUpdate = value => {
        updateTask(edit.id, value);
        setEdit({
            id:null,
            value : ""
        });
    }
    if(edit.id){
        return <AddTask edit = {edit} onSubmit={submitUpdate}/>
    }
  return props.tasks.map((task,index) =>(
    <div className={task.isComplete ? 'task-row complete':'task-row'} key={index}>
        <div key={task.id} onClick={()=>props.completeTask(task.id)}>
            {task.text}
        </div>
        <div className="icons">
            <RiCloseCircleLine onClick={()=> removeTask(task.id)} className="delete-icon"/>
            <TiEdit onClick={()=>setEdit({id:task.id,})}/>
        </div>
    </div>
  ))
}

export default Todo
