import React from 'react';
import Button from './Button';

function AddTask(){
    return (
        <div className="task-card">
            <form action="/" className=''>
                <label htmlFor="task-title">Add Task</label>
                <input type="text" name="task-title" id='task-title' />
                <label htmlFor="task-desc">Add Description</label>
                <textarea name="task-desc" id="task-desc" cols="30" rows="5"></textarea>
            </form>
            <Button name="Add Task"></Button>
        </div>
    )
}

export default AddTask;
