  }
  return (
    <div className="task-card">
      <h4 className="card-heading">Add Task</h4>
      <form action="/" className="add-task">
        <input
          type="text"
          name="task-title"
          id="task-title"
          placeholder="Add Task Title"
          required
        />
        <textarea
          name="task-desc"
          id="task-desc"
          cols="30"
          rows="5"
          placeholder="Add Description of task"
          maxLength={90}
          required
        ></textarea>
      </form>
      <button id="add-task-btn" className="btn btn-theme" onClick={()=>handleAddTask()}>Add Note</button>
    </div>
  );
}

export default AddTask;
